const popUp=document.querySelector(".popup"),popUpContent=document.querySelector(".popup__info"),buttonPopUp=document.querySelector(".info__text__button"),popUpClose=document.querySelector(".popupclose"),body=document.querySelector("body");function open(){popupPaddingRight(),popUp.classList.add("_open"),popUpContent.classList.add("_open"),document.body.classList.add("_lock")}function close(){popUp.classList.remove("_open"),popUpContent.classList.remove("_open"),document.body.classList.remove("_lock"),body.style.paddingRight="0px"}let popupPaddingRight=function(){let o=window.innerWidth-document.querySelector(".wrapper").clientWidth+"px";body.style.paddingRight=o};buttonPopUp.addEventListener("click",o=>{open(),window.onclick=function(o){o.target==popUp&&close()}}),popUpClose.addEventListener("click",o=>{close()});